import './App.css';



class App extends Component {

  constructor(props) {
    super(props)

    this.state = {
       Users:[]
    }
  }
  /*
componentDidMount(){
  fetch('http://localhost:9000/testApi').then(res => res.text())
  .then(res => this.setState ({
    apiResponse: res
  })).catch(err => err)
}
*/
componentDidMount(){
  fetch('http://localhost:9000/users/').then(res => res.json())
  .then(res => this.setState ({
    Users: res
  })).catch(err => err)
}


  render() {
    return (
      <div>
        <h1>API Response</h1>
    <p>{this.state.Users}</p>
      </div>
    )
  }
}


export default App;
