import React, { Component } from 'react'
import '../src/addUser.css';

class AddUser extends Component {

    constructor(props) {
        super(props)

        this.state = {
             User : {
                 name : '',
                 course:'',
                 email:'',
                 id :''
             }
        }
    }
    handleChange = (event)=>{
        const name= event.target.name;
        const value= event.target.value;
        let User ={...this.state.User};
        User[name]=value;
      this.setState({
        User 
      });
        console.log(this.state.User);
    }

    addHandler = (event)=>{
        event.preventDefault();
        try{
          let result= fetch('http://localhost:9000/users/add',{
            method: 'POST',

          headers:{
              'Accept':'application/json',
              'Content-Type': 'application/json'
          },
          body:JSON.stringify(this.state.User),
          });
          console.log(result);

        }catch(e){
            console.log(e);
        }
       this.setState({
           User:{}
       })
      }

    render() {

        return (
        <div>
             <NavBar />
            <h1>Form to add new User</h1>
        <form >
        <div className="wrapper">
        <label>Name</label>
        <input type="text" name="name" value={this.state.User.name} onChange={this.handleChange}></input>
        </div>
        <div className="wrapper">
        <label >email</label>
        <input type="email" name="email" value={this.state.User.email} onChange={this.handleChange}></input>
        </div>
        <div className="wrapper">
        <label>course</label>
        <input type="text" name="course" value={this.state.User.course} onChange={this.handleChange}></input>
        </div>
        <div className="wrapper">
        <label>id</label>
        <input type="number" name="id" value={this.state.User.id} onChange={this.handleChange}></input>
        </div>
          <button type="submit" onClick={this.addHandler}>Add</button>
          </form>
 </div>
        )
    }
}


export default AddUser;
