import React, { Component } from "react";
import AddUser from "./AddUser";
import "./App.css";
import User from "./User";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      apiResponse: "",
      Users: [],
    };
  }
  /*
componentDidMount(){
  fetch('http://localhost:9000/testApi').then(res => res.text())
  .then(res => this.setState ({
    apiResponse: res
  })).catch(err => err)
}
*/

  componentDidMount() {
    fetch("http://localhost:9000/users")
      .then((res) => res.json())
      .then((data) => {
        this.setState({
          Users: data,
        });
      });
  }

  render() {
    return (
      <div>
        <h1>Users List</h1>
        {this.state.apiResponse}
        <ul>
          {this.state.Users.map((user) => (
            <li>
              Name : {user.name}
              <br></br>
              Course: {user.course}
              <button>Edit</button>
              <button>Delete</button>
            </li>
          ))}
        </ul>

        <AddUser />
      </div>
    );
  }
}

export default App;
