componentDidMount(){
  fetch('http://localhost:9000/users').then(res => res.json())
  .then(data => {
      this.setState({
        Users : data
      })
    });
}
addHandler = ()=>{
  alert('Hello');
}

  render() {


    return (
      <div>
        <h1>API Response</h1>
        <button className="btn btn-primary " onClick={this.addHandler}> Add New User </button>

        {this.state.apiResponse}
        <ul>
    {this.state.Users.map(user => (<li>
      Name : {user.name}
      <br></br>
      Course: {user.course}

    </li>))}
        </ul>



      </div>
    )
  }
}


export default App;
